import { useState, useEffect } from 'react';
import ErrorMessage from "../errorMessage/ErrorMessage";

const ErrorBoundary = (props) => {
    const [error, setError] = useState(false);
    
    useEffect(() => {
        setError(true);
    }, [error]);

    if (setError(error => true)) {
        return <ErrorMessage/>
    }
    props.children;
}

export default ErrorBoundary;
