class MarvelService {
    _apiBase = 'https://gateway.marvel.com:443/v1/public/'
    getResourse = async (url ) => {
        let res = await fetch(url);
    
        if (!res.ok) {
            throw new Error(`Could not fetch ${url}, status:  ${res.status}`);
        }
    
        return await res.json();
    };

    getAllCharacters = () => {
        return this.getResourse('characters?limit=9&offset=300&apikey=f75e1499b218241b71745986222174a5');
    }

    getCharacter = (id) => {
        return this.getResourse(`characters/${id}?apikey=f75e1499b218241b71745986222174a5`);
    }
}

export default MarvelService;
